mapper.select:
SELECT cs.contractCode as contractCode, csf.contractorName as contractorName, cs.contractorCode as contractorCode, cs.contractorDevision as contractorDevision, cs.contractState as contractState, cs.contractSingningDate as contractSingningDate, cs.contractorId as contractorId, cs.memberId as memberId FROM k3_tb_contract_simple AS cs Inner join k3_tb_contractor_staff AS csf on cs.contractorId = csf.contractorId
cs.contractSingningDate BETWEEN #{contractSingningDateStart} AND #{contractSingningDateEnd}
AND cs.${searchKey} LIKE CONCAT('%', #{searchValue}, '%')
AND csf.${searchKey} LIKE CONCAT('%', #{searchValue}, '%')
ORDER BY cs.contractSingningDate DESC
LIMIT #{paramMap.startNum}, #{paramMap.rowPerPage};

