mapper.select:
SELECT w.warehousingCode, p.productName, w.laydownGoodsCount, w.manufacturedDate, w.expiratonDate, c.contractorName, w.laydownDate, w.laydownCheckCode, IFNULL(w.laydownCheckResult,'') AS laydownCheckResult, w.laydownCheckDate, m.memberName, w.laydownCheckResult FROM k3_tb_warehousing AS w INNER JOIN k3_tb_product AS p ON w.productCode = p.productCode INNER JOIN k3_tb_contractor_staff AS c on w.contractorId = c.contractorId INNER JOIN k3_tb_member AS m ON w.laydownCheckManager = m.memberId
${searchCondition.laydownKey} LIKE CONCAT('%',#{searchCondition.laydownValue},'%')
AND ${searchCondition.laydownDateKey} >= #{searchCondition.searchStartDate}
AND #{searchCondition.searchEndDate} >= ${searchCondition.laydownDateKey}
AND w.laydownCheckResult is not null
WHERE w.laydownCheckResult is not null
LIMIT #{paramMap.startNum}, #{paramMap.rowPerPage};

SELECT COUNT(1) FROM k3_tb_warehousing AS w INNER JOIN k3_tb_contractor_staff AS c on w.contractorId = c.contractorId INNER JOIN k3_tb_member AS m ON w.laydownCheckManager = m.memberId INNER JOIN k3_tb_product AS p ON w.productCode = p.productCode WHERE w.laydownCheckResult is not null
AND ${searchCondition.laydownKey} LIKE CONCAT('%',#{searchCondition.laydownValue},'%')
AND ${searchCondition.laydownDateKey} >= #{searchCondition.searchStartDate}
AND #{searchCondition.searchEndDate} >= ${searchCondition.laydownDateKey}
