mapper.select:
SELECT r.releaseCode ,r.releaseMergeCode ,p.productName ,r.releaseGoodsCount ,cs.contractorName
,r.shipmentCheckResult ,r.releaseDate ,r.shipmentCheckManager ,r.shipmentCheckRegDate
FROM k3_tb_release AS r INNER JOIN k3_tb_product AS p ON r.productCode = p.productCode INNER JOIN k3_tb_contractor_staff AS cs ON r.contractorId = cs.contractorId WHERE releaseCode = #{releaseCode};

SELECT r.releaseCode, p.productName, r.releaseGoodsCount, cs.contractorName, r.acceptDate, r.releaseDate, r.recipientName, r.shipmentCheckManager, r.shipmentCheckRegDate, r.shipmentCheckResult FROM k3_tb_release AS r INNER JOIN k3_tb_stock AS s ON r.stockCode = s.stockCode INNER JOIN k3_tb_warehousing AS w ON s.warehousingCode = w.warehousingCode INNER JOIN k3_tb_contractor_staff AS cs ON w.contractorId = cs.contractorId INNER JOIN k3_tb_product AS p ON w.productCode = p.productCode
${searchCondition.shipmentKey} LIKE CONCAT('%',#{searchCondition.shipmentValue},'%')
AND ${searchCondition.shipmentDateKey} >= #{searchCondition.searchStartDate}
AND #{searchCondition.searchEndDate} >= ${searchCondition.shipmentDateKey}
AND r.releaseAllow = 'Y' AND r.shipmentCheckResult IS NOT NULL
WHERE r.releaseAllow = 'Y' AND r.shipmentCheckResult IS NOT NULL
LIMIT #{paramMap.startNum}, #{paramMap.rowPerPage};

SELECT COUNT(1) FROM k3_tb_release AS r INNER JOIN k3_tb_stock AS s ON r.stockCode = s.stockCode INNER JOIN k3_tb_warehousing AS w ON s.warehousingCode = w.warehousingCode INNER JOIN k3_tb_contractor_staff AS cs ON w.contractorId = cs.contractorId INNER JOIN k3_tb_product AS p ON w.productCode = p.productCode WHERE r.releaseAllow = 'Y' AND r.shipmentCheckResult IS NOT NULL;
AND ${searchCondition.shipmentKey} LIKE CONCAT('%',#{searchCondition.shipmentValue},'%')
AND ${searchCondition.shipmentDateKey} >= #{searchCondition.searchStartDate}
AND #{searchCondition.searchEndDate} >= ${searchCondition.shipmentDateKey}
