mapper.select:
SELECT r.releaseCode ,r.releaseMergeCode ,p.productName ,r.releaseGoodsCount ,cs.contractorName
,r.shipmentCheckResult ,r.releaseDate ,r.shipmentCheckManager ,r.shipmentCheckRegDate
FROM k3_tb_release AS r INNER JOIN k3_tb_product AS p ON r.productCode = p.productCode INNER JOIN k3_tb_contractor_staff AS cs ON r.contractorId = cs.contractorId WHERE releaseCode = #{releaseCode};
