mapper.update:
UPDATE 
	k3_tb_stock
SET
	stockQuantity = #{stockQuantity}
WHERE
	stockCode = #{stockCode};

mapper.select:
SELECT s.stockCode, w.laydownCheckCode, p.productName, s.stockQuantity, c.contractorName, p.categoryCode, t.largeCategory, t.mediumCategory, t.smallCategory, s.locationCode, s.pileupDate, m.memberName AS pileupManagerName FROM k3_tb_stock AS s INNER JOIN k3_tb_warehousing AS w ON s.warehousingCode = w.warehousingCode INNER JOIN k3_tb_product AS p ON w.productCode = p.productCode INNER JOIN k3_tb_contractor_staff AS c ON w.contractorId = c.contractorId INNER JOIN k3_tb_total_category AS t ON p.categoryCode = t.categoryCode INNER JOIN k3_tb_member AS m ON s.pileupManager = m.memberId
AND ${searchCondition.detailStockKey} LIKE CONCAT('%',#{searchCondition.detailStockValue},'%')
AND ${searchCondition.categoryKey} LIKE CONCAT('%',#{searchCondition.categoryValue},'%')
AND ${searchCondition.stockDateKey} >= #{searchCondition.searchStartDate}
AND #{searchCondition.searchEndDate} >= ${searchCondition.stockDateKey}
LIMIT #{paramMap.startNum}, #{paramMap.rowPerPage};

SELECT COUNT(1) FROM k3_tb_stock AS s INNER JOIN k3_tb_warehousing AS w ON s.warehousingCode = w.warehousingCode INNER JOIN k3_tb_product AS p ON w.productCode = p.productCode INNER JOIN k3_tb_contractor_staff AS c ON w.contractorId = c.contractorId INNER JOIN k3_tb_total_category AS t ON p.categoryCode = t.categoryCode INNER JOIN k3_tb_member AS m ON s.pileupManager = m.memberId
AND ${searchCondition.detailStockKey} LIKE CONCAT('%',#{searchCondition.detailStockValue},'%')
AND ${searchCondition.categoryKey} LIKE CONCAT('%',#{searchCondition.categoryValue},'%')
AND ${searchCondition.stockDateKey} >= #{searchCondition.searchStartDate}
AND #{searchCondition.searchEndDate} >= ${searchCondition.stockDateKey}
