mapper.select:
SELECT w.warehousingCode, p.productName, w.laydownGoodsCount, w.manufacturedDate, w.expiratonDate, c.contractorName, w.laydownDate, w.laydownCheckCode, IFNULL(w.laydownCheckResult,'') AS laydownCheckResult, w.laydownCheckDate, m.memberName, w.laydownCheckResult, w.sorting FROM k3_tb_warehousing AS w INNER JOIN k3_tb_product AS p ON w.productCode = p.productCode INNER JOIN k3_tb_contractor_staff AS c on w.contractorId = c.contractorId INNER JOIN k3_tb_member AS m ON w.laydownCheckManager = m.memberId
${searchCondition.warehousingKey} LIKE CONCAT('%',#{searchCondition.warehousingValue},'%')
AND ${searchCondition.warehousingDateKey} >= #{searchCondition.searchStartDate}
AND #{searchCondition.searchEndDate} >= ${searchCondition.warehousingDateKey}
And w.warehousingAllow = 'Y' AND w.laydownCheckResult = 'Y'
WHERE w.warehousingAllow = 'Y' and w.laydownCheckResult = 'Y'
LIMIT #{paramMap.startNum}, #{paramMap.rowPerPage};

SELECT COUNT(1) FROM k3_tb_warehousing AS w INNER JOIN k3_tb_contractor_staff AS c on w.contractorId = c.contractorId INNER JOIN k3_tb_product AS p ON w.productCode = p.productCode WHERE w.warehousingAllow = 'Y' AND w.laydownCheckResult = 'Y'
AND ${searchCondition.warehousingKey} LIKE CONCAT('%',#{searchCondition.warehousingValue},'%')
AND ${searchCondition.warehousingDateKey} >= #{searchCondition.searchStartDate}
AND #{searchCondition.searchEndDate} >= ${searchCondition.warehousingDateKey}
