mapper.select:
SELECT w.warehousingCode, w.laydownGoodsName, w.laydownGoodsCount, w.manufacturedDate, w.expiratonDate, c.contractorName, w.laydownDate, w.laydownCheckCode, w.laydownCheckResult, w.warehousingDay, w.laydownCheckDate, m.memberName, w.laydownCheckResult FROM k3_tb_warehousing AS w INNER JOIN k3_tb_contractor_staff AS c on w.contractorId = c.contractorId INNER JOIN k3_tb_member AS m ON w.laydownCheckManager = m.memberId
${laydownKey} LIKE CONCAT('%',#{laydownValue},'%')
AND ${laydownDateKey} >= #{searchStartDate}
AND #{searchEndDate} >= ${laydownDateKey}
WHERE w.laydownCheckResult is not null;
LIMIT #{startNum}, #{rowPerPage};

SELECT w.warehousingCode, w.laydownGoodsName, w.laydownGoodsCount, w.manufacturedDate, w.expiratonDate, c.contractorName, w.laydownDate, w.laydownCheckCode, w.laydownCheckResult, w.warehousingDay, w.laydownCheckDate, m.memberName, w.laydownCheckResult, w.sorting FROM k3_tb_warehousing AS w INNER JOIN k3_tb_contractor_staff AS c on w.contractorId = c.contractorId INNER JOIN k3_tb_member AS m ON w.laydownCheckManager = m.memberId
${warehousingKey} LIKE CONCAT('%',#{warehousingValue},'%')
AND ${warehousingDateKey} >= #{searchStartDate}
AND #{searchEndDate} >= ${warehousingDateKey}
WHERE w.warehousingAllow = 'Y'
LIMIT #{startNum}, #{rowPerPage};

SELECT COUNT(1) FROM k3_tb_warehousing AS w INNER JOIN k3_tb_contractor_staff AS c on w.contractorId = c.contractorId INNER JOIN k3_tb_member AS m ON w.laydownCheckManager = m.memberId WHERE w.laydownCheckResult is not null
AND ${laydownKey} LIKE CONCAT('%',#{laydownValue},'%')
AND ${laydownDateKey} >= #{searchStartDate}
AND #{searchEndDate} >= ${laydownDateKey}
WHERE w.laydownCheckResult is not null;

SELECT COUNT(1) FROM k3_tb_warehousing AS w INNER JOIN k3_tb_contractor_staff AS c on w.contractorId = c.contractorId WHERE w.warehousingAllow = 'Y'
AND ${warehousingKey} LIKE CONCAT('%',#{warehousingValue},'%')
AND ${warehousingDateKey} >= #{searchStartDate}
AND #{searchEndDate} >= ${warehousingDateKey}
WHERE w.warehousingAllow = 'Y'
