mapper.select:
SELECT w.warehousingCode, w.laydownGoodsName, w.laydownGoodsCount, w.manufacturedDate, w.expiratonDate, c.contractorName, w.laydownDate, w.laydownCheckCode, IFNULL(w.laydownCheckResult,'') AS laydownCheckResult, w.warehousingDay, w.laydownCheckDate, m.memberName, w.laydownCheckResult FROM k3_tb_warehousing AS w INNER JOIN k3_tb_contractor_staff AS c on w.contractorId = c.contractorId INNER JOIN k3_tb_member AS m ON w.laydownCheckManager = m.memberId
${searchCondition.laydownKey} LIKE CONCAT('%',#{searchCondition.laydownValue},'%')
AND ${searchCondition.laydownDateKey} >= #{searchCondition.searchStartDate}
AND #{searchCondition.searchEndDate} >= ${searchCondition.laydownDateKey}
WHERE w.laydownCheckResult is not null;
LIMIT #{searchCondition.startNum}, #{searchCondition.rowPerPage};

SELECT w.warehousingCode, w.laydownGoodsName, w.laydownGoodsCount, w.manufacturedDate, w.expiratonDate, c.contractorName, w.laydownDate, w.laydownCheckCode, IFNULL(w.laydownCheckResult,'') AS laydownCheckResult, w.warehousingDay, w.laydownCheckDate, m.memberName, w.laydownCheckResult, w.sorting FROM k3_tb_warehousing AS w INNER JOIN k3_tb_contractor_staff AS c on w.contractorId = c.contractorId INNER JOIN k3_tb_member AS m ON w.laydownCheckManager = m.memberId
${searchCondition.warehousingKey} LIKE CONCAT('%',#{searchCondition.warehousingValue},'%')
AND ${searchCondition.warehousingDateKey} >= #{searchCondition.searchStartDate}
AND #{searchCondition.searchEndDate} >= ${searchCondition.warehousingDateKey}
WHERE w.warehousingAllow = 'Y'
LIMIT #{searchCondition.startNum}, #{searchCondition.rowPerPage};

SELECT COUNT(1) FROM k3_tb_warehousing AS w INNER JOIN k3_tb_contractor_staff AS c on w.contractorId = c.contractorId INNER JOIN k3_tb_member AS m ON w.laydownCheckManager = m.memberId WHERE w.laydownCheckResult is not null
AND ${searchCondition.laydownKey} LIKE CONCAT('%',#{searchCondition.laydownValue},'%')
AND ${searchCondition.laydownDateKey} >= #{searchCondition.searchStartDate}
AND #{searchCondition.searchEndDate} >= ${searchCondition.laydownDateKey}

SELECT COUNT(1) FROM k3_tb_warehousing AS w INNER JOIN k3_tb_contractor_staff AS c on w.contractorId = c.contractorId WHERE w.warehousingAllow = 'Y'
AND ${searchCondition.warehousingKey} LIKE CONCAT('%',#{searchCondition.warehousingValue},'%')
AND ${searchCondition.warehousingDateKey} >= #{searchCondition.searchStartDate}
AND #{searchCondition.searchEndDate} >= ${searchCondition.warehousingDateKey}
